public class MainClass04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 7; i++) { // i o
			
			for(int j = 0; j < 7; j++) {  // j -0:* 1:*.... 6:*
				System.out.print("*");	  //
										  //......
										  // i:6j - 0:* 1:*....6:*
			}
		
				System.out.println(); //개행 줄이동
		}
	}
}
